mapper.select:
SELECT SQL_CALC_FOUND_ROWS las.`id`,las.`ip`,las.`desc`,las.`name`,las.`port`,las.`department`,ld.`name` depart_name FROM lsblj_apppub_server las LEFT JOIN `lsblj_department` ld ON las.`department` = ld.id WHERE 1 AND( las.`name` like CONCAT('%',#{sname},'%') or las.`ip` like CONCAT('%',#{sname},'%') or las.`desc` like CONCAT('%',#{sname},'%') or ld.`name` like CONCAT('%',#{sname},'%') ) AND las.`name` like CONCAT('%',#{sname},'%') AND las.`ip` like CONCAT('%',#{sname},'%') AND ld.`name` like CONCAT('%',#{sname},'%')
AND las.department in
#{item} OR las.department=0
AND las.`desc` like CONCAT('%',#{sname},'%')
ORDER BY las.name ASC LIMIT #{page_start},#{page_length}
;SELECT found_rows() AS recordCounts;

		select * from lsblj_apppub_server a INNER JOIN (SELECT id ,parent_id FROM lsblj_department ld,
        (SELECT @id :=#{id}) pd
        WHERE (FIND_IN_SET( parent_id, @id ) > 0 AND @id := CONCAT( @id, ',', id )) OR id = #{id}) T ON a.department=T.id;

