mapper.select:
SELECT SQL_CALC_FOUND_ROWS DATE_FORMAT(FROM_UNIXTIME(start),'%Y-%m-%d %H:%i:%s') start,DATE_FORMAT(FROM_UNIXTIME(end),'%Y-%m-%d %H:%i:%s') end,r.* FROM lsblj_device_record r LEFT JOIN lsblj_protocol p ON r.protocol_id=p.id where r.id NOT IN(SELECT record_id FROM lsblj_live_sessions) AND p.`name` like CONCAT('%',#{deviceRecord.protocol},'%') AND `client_ip` like CONCAT('%',#{deviceRecord.client_ip},'%') AND `device_ip` like CONCAT('%',#{deviceRecord.device_ip},'%') AND `device_name` like CONCAT('%',#{deviceRecord.device_name},'%') AND `username` like CONCAT('%',#{deviceRecord.username},'%') AND `realname` like CONCAT('%',#{deviceRecord.realname},'%') AND `device_username` like CONCAT('%',#{deviceRecord.device_username},'%') AND `start` >= UNIX_TIMESTAMP(#{deviceRecord.start}) AND `start` <= UNIX_TIMESTAMP(#{deviceRecord.end}) AND `device_username` like CONCAT('%',#{deviceRecord.device_username},'%') AND (p.`name` like CONCAT('%',#{deviceRecord.searchAll },'%') or `client_ip` like CONCAT('%',#{deviceRecord.searchAll },'%') or `device_ip` like CONCAT('%',#{deviceRecord.searchAll },'%') or `device_name` like CONCAT('%',#{deviceRecord.searchAll },'%') or `username` like CONCAT('%',#{deviceRecord.searchAll },'%') or `device_username` like CONCAT('%',#{deviceRecord.searchAll },'%') )
AND EXISTS( SELECT * FROM lsblj_device_record_command where command like CONCAT('%',#{deviceRecord.command},'%') AND lsblj_device_record_id=r.id )
ORDER BY start DESC
LIMIT #{page_start},#{page_length}
;SELECT found_rows() AS recordCounts;

SELECT DATE_FORMAT(FROM_UNIXTIME(start),'%Y-%m-%d %H:%i:%s') start,DATE_FORMAT(FROM_UNIXTIME(end),'%Y-%m-%d %H:%i:%s') end,lsblj_device_record.*,lsblj_protocol.name protocol_name 
FROM lsblj_device_record LEFT JOIN lsblj_protocol ON lsblj_device_record.protocol_id=lsblj_protocol.id 
where lsblj_device_record.id=#{id}

SELECT SQL_CALC_FOUND_ROWS date_format(FROM_UNIXTIME(start),"%Y-%m-%d %H") `interval`, count(distinct device_ip) deviceips,count(distinct username) users 
	FROM lsblj_device_record 
	WHERE 1 

		AND login_datetime >= #{start_date}


		AND login_datetime <= #{end_date} 

	 GROUP BY date_format(FROM_UNIXTIME(start),"%Y%m%d%H")
	 ORDER BY date_format(FROM_UNIXTIME(start),"%Y%m%d%H") ASC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS date_format(FROM_UNIXTIME(start),"%Y-%m-%d") `interval`, count(distinct device_ip) deviceips,count(distinct username) users 
	FROM lsblj_device_record 
	WHERE 1 

		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") >= #{start_date}


		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") <= #{end_date} 

	 GROUP BY date_format(FROM_UNIXTIME(start),"%Y%m%d")
	ORDER BY date_format(FROM_UNIXTIME(start),"%Y%m%d") ASC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS date_format(FROM_UNIXTIME(start),"%Y-%v") `interval`, count(distinct device_ip) deviceips,count(distinct username) users 
	FROM lsblj_device_record 
	WHERE 1 

		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") >= #{start_date}


		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") <= #{end_date} 

	 GROUP BY date_format(FROM_UNIXTIME(start),"%Y%v")
	ORDER BY date_format(FROM_UNIXTIME(start),"%Y%v") ASC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS date_format(FROM_UNIXTIME(start),"%Y-%m") `interval`, count(distinct device_ip) deviceips,count(distinct username) users 
	FROM lsblj_device_record 
	WHERE 1 

		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") >= #{start_date}


		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") <= #{end_date} 

	 GROUP BY date_format(FROM_UNIXTIME(start),"%Y%m")
	ORDER BY date_format(FROM_UNIXTIME(start),"%Y%m") ASC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS device_ip, device_name, sum(time_length) total 
	FROM lsblj_device_record 
	WHERE 1 

		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") >= #{start_date}


		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") <= #{end_date} 

	GROUP BY device_ip 
	ORDER BY sum(time_length) DESC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS username, realname, sum(time_length) total 
	FROM lsblj_device_record 
	WHERE 1 

		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") >= #{start_date}


		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") <= #{end_date} 

	GROUP BY username 
	ORDER BY sum(time_length) DESC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS command, count(*) total 
	FROM lsblj_record_command 
	WHERE command!=''  

		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") >= #{start_date}


		AND date_format(FROM_UNIXTIME(start),"%Y-%m-%d") <= #{end_date} 

	GROUP BY command  
	ORDER BY count(*) DESC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS date_format(FROM_UNIXTIME(operate_datetime),"%Y%m%d %H") `interval`, sum(if(protocol='ssh',1,0)) ssh, sum(if(protocol!='ssh',1,0)) system
	FROM lsblj_alert_log 
	WHERE 1 

		AND operate_datetime >= #{start_date}


		AND operate_datetime <= #{end_date} 

	 GROUP BY date_format(FROM_UNIXTIME(operate_datetime),"%Y%m%d%H")
	 ORDER BY date_format(FROM_UNIXTIME(operate_datetime),"%Y%m%d%H") ASC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

