mapper.insert:
INSERT INTO lsblj_user
`role_id`=#{role_id}, `username`=#{username}, `realname`=#{realname}, `ldap_dn`=#{ldap_dn}, `avatar`=#{avatar}, `password`=AES_ENCRYPT(#{password},(select aeskey from lsblj_config_password_encrypt_key limit 1)), `status`=#{status}, `groupid`=#{groupid}, `fail_count`=#{fail_count}, `lock_time`=#{lock_time}, `last_chpass`=#{last_chpass}, `email`=#{email}, `mobile`=#{mobile}, `qq`=#{qq}, `wechat`=#{wechat}, `creator_id`=#{creator_id}, `create_time`=#{create_time}, `description`=#{description}, `local_auth`=#{local_auth}, `sms_auth`=#{sms_auth}, `radius_auth`=#{radius_auth}, `dynamic_auth`=#{dynamic_auth}, `finger_auth`=#{finger_auth}, `ldap_auth`=#{ldap_auth}, `ad_auth`=#{ad_auth}, `wechat_auth`=#{wechat_auth}, `email_auth`=#{email_auth}, `auth_type`=#{auth_type}, `last_login_ip`=#{last_login_ip}, `last_change_password`=#{last_change_password}, `department`=#{department}, `depart_name`=#{depart_name}, `type`=#{type}, `resolution`=#{resolution}, `rdpdisk`=#{rdpdisk}, `session_title`=#{session_title}, `google_auth_key`=#{google_auth_key}, `sms_code`=#{sms_code}, `valid_datetime_start`=#{valid_datetime_start}, `valid_long`=#{valid_long}, `valid_datetime_end`=#{valid_datetime_end},
;

mapper.update:
UPDATE lsblj_user
`role_id`=#{role_id}, `username`=#{username}, `realname`=#{realname}, `ldap_dn`=#{ldap_dn}, `depart_name`=#{depart_name}, `avatar`=#{avatar}, `password`=AES_ENCRYPT(#{password},(select aeskey from lsblj_config_password_encrypt_key limit 1)), `status`=#{status}, `groupid`=#{groupid}, `fail_count`=#{fail_count}, `lock_time`=#{lock_time}, `last_chpass`=#{last_chpass}, `email`=#{email}, `mobile`=#{mobile}, `qq`=#{qq}, `wechat`=#{wechat}, `creator_id`=#{creator_id}, `create_time`=#{create_time}, `description`=#{description}, `local_auth`=#{local_auth}, `sms_auth`=#{sms_auth}, `radius_auth`=#{radius_auth}, `dynamic_auth`=#{dynamic_auth}, `finger_auth`=#{finger_auth}, `ldap_auth`=#{ldap_auth}, `ad_auth`=#{ad_auth}, `wechat_auth`=#{wechat_auth}, `email_auth`=#{email_auth}, `auth_type`=#{auth_type}, `last_login_ip`=#{last_login_ip}, `last_change_password`=#{last_change_password}, `department`=#{department}, `type`=#{type}, `resolution`=#{resolution}, `rdpdisk`=#{rdpdisk}, `session_title`=#{session_title}, `google_auth_key`=#{google_auth_key}, `google_auth_token_used`=#{google_auth_token_used}, `sms_code`=#{sms_code}, `valid_datetime_start`=#{valid_datetime_start}, `valid_long`=#{valid_long}, `valid_datetime_end`=#{valid_datetime_end},
where id=#{id};

mapper.delete:
        DELETE FROM lsblj_user
        where id>3 and id IN(

            #{item},

		0);
		DELETE FROM lsblj_group_user WHERE user_id NOT IN (SELECT id FROM lsblj_user);
		DELETE FROM lsblj_access_policy_user WHERE user_id NOT IN (SELECT id FROM lsblj_user);
		DELETE  FROM lsblj_cmd_policy_user WHERE user_id NOT IN (SELECT id FROM lsblj_user);

mapper.select:
SELECT SQL_CALC_FOUND_ROWS , lsblj_user.description description,lsblj_role.name rolename, lsblj_department.name depart_name FROM lsblj_user LEFT JOIN lsblj_department ON lsblj_user.department=lsblj_department.id LEFT JOIN lsblj_role ON lsblj_user.role_id=lsblj_role.id where 1 AND lsblj_role.name like CONCAT('%',#{user.rolename},'%') AND `username` like CONCAT('%',#{user.username},'%') AND lsblj_department.`name` like CONCAT('%',#{user.depart_name},'%')
AND `realname` like CONCAT('%',#{user.realname},'%') AND `status` like CONCAT('%',#{user.status},'%')
AND ( lsblj_role.name like CONCAT('%',#{user.searchall},'%') or `username` like CONCAT('%',#{user.searchall},'%') or lsblj_department.`name` like CONCAT('%',#{user.searchall},'%')
or `status` like CONCAT('%',#{user.searchall},'%') or `realname` like CONCAT('%',#{user.searchall},'%') or `status` like CONCAT('%',#{user.searchall},'%') )
AND department in
#{item}
ORDER BY id ASC
LIMIT #{page_start},#{page_length}
;SELECT found_rows() AS recordCounts;

mapper.delete:
DELETE FROM `lsblj_user` WHERE department = #{department};
DELETE FROM lsblj_group_user WHERE user_id NOT IN (SELECT id FROM lsblj_user);
DELETE FROM lsblj_access_policy_user WHERE user_id NOT IN (SELECT id FROM lsblj_user);
DELETE  FROM lsblj_cmd_policy_user WHERE user_id NOT IN (SELECT id FROM lsblj_user);

update.foreach:
UPDATE lsblj_user
`role_id`=#{item.role_id}, `username`=#{item.username}, `realname`=#{item.realname}, `department`=#{item.department}, `password`=AES_ENCRYPT(#{item.password},(select aeskey from lsblj_config_password_encrypt_key limit 1)), `groupid`=#{item.groupid}, `email`=#{item.email}, `mobile`=#{item.mobile}, `qq`=#{item.qq}, `wechat`=#{item.wechat},
where id=#{item.id};
