mapper.update:
UPDATE lsblj_change_password_log
`id`=#{id}, `time`=#{time}, `device_ip`=#{device_ip}, `username`=#{username}, `device_name`=#{device_name}, `update_success_flag`=#{update_success_flag}, `password`=AES_ENCRYPT(#{password},(select aeskey from lsblj_config_password_encrypt_key limit 1)), `change_pa`=#{change_pa}, `end_time`=#{end_time},
where id=#{id}

mapper.select:
SELECT 
SQL_CALC_FOUND_ROWS 
a.*,b.name policy_name FROM lsblj_change_password_log a LEFT JOIN lsblj_change_password_policy b ON a.policy_id=b.id 

LIMIT #{page_start},#{page_length}

;SELECT found_rows() AS recordCounts;
