SELECT SQL_CALC_FOUND_ROWS DATE_FORMAT(FROM_UNIXTIME(login_datetime),"%Y-%m-%d") login_datetime,lsblj_login_log.* FROM lsblj_login_log where 1 AND source_ip like CONCAT('%',#{loginLog.source_ip},'%') AND `username` like CONCAT('%',#{loginLog.username},'%') AND `realname` like CONCAT('%',#{loginLog.realname},'%') AND `protocol` like CONCAT('%',#{loginLog.protocol},'%') AND `status` like CONCAT('%',#{loginLog.status},'%') AND ( `source_ip` like CONCAT('%',#{loginLog.searchAll },'%') or `username` like CONCAT('%',#{loginLog.searchAll },'%') or `realname` like CONCAT('%',#{loginLog.searchAll },'%') or `protocol` like CONCAT('%',#{loginLog.searchAll },'%') or `status` like CONCAT('%',#{loginLog.searchAll },'%') )
ORDER BY login_datetime DESC
LIMIT #{page_start},#{page_length}
;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS DATE_FORMAT(FROM_UNIXTIME(TT.login_datetime),#{time_format}) login_datetime,TT.protocol,TT.username, TT.realname ,TT.source_ip,TT.`status`,TT.result,TT.details FROM ( SELECT 'web' protocol, username, realname, login_datetime ,source_ip,`status`,result,details FROM lsblj_login_log UNION ALL SELECT p.name protocol, username, realname, `start` login_datetime, device_ip,`status`,IF(`status`=1,'成功','失败') result,IF(`status`=1,'登录成功','登录失败') details FROM lsblj_device_record dr LEFT JOIN lsblj_protocol p ON dr.protocol_id=p.id UNION ALL SELECT 'apppub' protocol, username, realname, `start` login_datetime, server_ip,`status`,IF(`status`=1,'成功','失败') result,IF(`status`=1,'登录成功','登录失败') details FROM lsblj_apppub_record ) TT where DATE_FORMAT(FROM_UNIXTIME(login_datetime),#{time_format}) like CONCAT('%',#{time},'%') AND source_ip like CONCAT('%',#{loginLog.source_ip},'%') AND `username` like CONCAT('%',#{loginLog.username},'%') AND `realname` like CONCAT('%',#{loginLog.realname},'%') AND `protocol` like CONCAT('%',#{loginLog.protocol},'%') AND `status` like CONCAT('%',#{loginLog.status},'%') AND ( `source_ip` like CONCAT('%',#{loginLog.searchAll },'%') or `username` like CONCAT('%',#{loginLog.searchAll },'%') or `realname` like CONCAT('%',#{loginLog.searchAll },'%') or `protocol` like CONCAT('%',#{loginLog.searchAll },'%') or `status` like CONCAT('%',#{loginLog.searchAll },'%') )
ORDER BY login_datetime DESC
LIMIT #{page_start},#{page_length}
;SELECT found_rows() AS recordCounts;

SELECT SQL_CALC_FOUND_ROWS date_format(FROM_UNIXTIME(login_datetime),"%Y-%m-%d %H") `interval`, count(*) total,count(IF(status=1,1,null)) successes,count(IF(status=0,1,null)) fails
		,count(distinct source_ip) sourceips,count(distinct username) users 
	FROM lsblj_login_log 
	WHERE 1 

		AND login_datetime >= #{start_date}


		AND login_datetime <= #{end_date} 

	 GROUP BY date_format(FROM_UNIXTIME(login_datetime),"%Y%m%d%H")
	ORDER BY date_format(FROM_UNIXTIME(login_datetime),"%Y%m%d%H") DESC

	LIMIT #{page_start},#{page_length}

	;SELECT found_rows() AS recordCounts;

